export default function Button({children, onClickHandler, styleType="none"}) {

  const styles = {
    "none": "bg-transparent text-white border-2 solid border-white rounded-md mt-12 py-6 px-12 text-xs font-semibold uppercase align-middle",
    "pill": "bg-transparent border-2 border-2 border-white border-solid px-2 rounded-full text-md"
  }

  return (
    <button
      className={styles[styleType]}
      onClick={onClickHandler}
    >
      {children}
    </button>
  )
}
