import { Link } from '@reach/router';


export default function Button({children, onClickHandler, to, styleType="none"}) {

  const styles = {
    "none": "bg-transparent text-white border-2 solid border-white rounded-md mt-12 py-6 px-12 text-xs font-semibold uppercase align-middle",
    "pill": "bg-transparent border-2 border-2 border-white border-solid px-6 rounded-full uppercase text-sm"
  }

  return (
    <>
      <Link to={to}>
        <button
          className={styles[styleType]}
          onClick={onClickHandler}
        >
          {children}
        </button>
      </Link>
    </>
  )
}
