import React from 'react';
import PropTypes from 'prop-types';

import styled from 'styled-components/macro';
import { theme, mixins } from '../styles';
import TrackDuration from "./Profile/TrackDuration";
import TrackArtwork from "./Profile/TrackArtwork";
import {Link} from "@reach/router";
const { colors, fontSizes } = theme;

const TrackMeta = styled.div`
  display: grid;
  grid-template-columns: 1fr max-content;
  grid-gap: 10px;
`;
const TrackName = styled.span`
  margin-bottom: 5px;
  border-bottom: 1px solid transparent;
  &:hover,
  &:focus {
    border-bottom: 1px solid ${colors.white};
  }
`;
const TrackAlbum = styled.div`
  ${mixins.overflowEllipsis};
  color: ${colors.lightGrey};
  font-size: ${fontSizes.sm};
  margin-top: 3px;
`;


const TrackItem = ({ track }) => (
  <li>

    <div className="grid grid-cols-4">

      <div className="col-span-1">
        <TrackArtwork track={track} />
      </div>


      <div className="col-span-3">
        <TrackMeta>
        <Link to={`/track/${track.id}`}>
          <span className="truncate pr-1">
            {track.name &&
              <span className="mb-2 border-b-2 border-solid border-transparent hover:border-b-2 hover:border-solid hover:border-white">
              {track.name}
            </span>}
            {track.artists && track.album && (
              <div className="truncate text-lightGrey text-sm mt-1 w-full">
                {track.artists &&
                  track.artists.map(({ name }, i) => (
                    <span key={i}>
                      {name}
                      {track.artists.length > 0 && i === track.artists.length - 1 ? '' : ','}&nbsp;
                    </span>
                  ))}
                &nbsp;&middot;&nbsp;&nbsp;
                <span className="truncate w-24">
                {track.album.name}
                </span>
              </div>
            )}
          </span>
          </Link>
          <TrackDuration track={track} />
        </TrackMeta>
      </div>
    </div>

  </li>
);

TrackItem.propTypes = {
  track: PropTypes.object.isRequired,
};

export default TrackItem;
