import React, { useState, useEffect } from 'react';
import { Link } from '@reach/router';
import { getPlaylists } from '../spotify';
import { catchErrors } from '../utils';

import Loader from './Loader';
import { IconMusic } from './icons';

import styled from 'styled-components/macro';
import { theme, mixins, media, Main } from '../styles';
const { colors, fontSizes, spacing } = theme;



const PlaylistImage = styled.img`
  object-fit: cover;
`;

const PlaceholderArtwork = styled.div`
  ${mixins.flexCenter};
  position: relative;
  width: 100%;
  padding-bottom: 100%;
  background-color: ${colors.darkGrey};
  svg {
    width: 50px;
    height: 50px;
  }
`;
const PlaceholderContent = styled.div`
  ${mixins.flexCenter};
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
`;
const PlaylistName = styled(Link)`
  display: inline;
  border-bottom: 1px solid transparent;
  &:hover,
  &:focus {
    border-bottom: 1px solid ${colors.white};
  }
`;
const TotalTracks = styled.div`
  text-transform: uppercase;
  margin: 5px 0;
  color: ${colors.lightGrey};
  font-size: ${fontSizes.xs};
  letter-spacing: 1px;
`;

const Playlists = () => {
  const [playlists, setPlaylists] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const { data } = await getPlaylists();
      setPlaylists(data);
    };
    catchErrors(fetchData());
  }, []);

  return (
    <main className="w-full mx-auto p-24">
      <h2 className="text-7xl">Your Playlists</h2>
      <div className="flex justify-between items-start">
        <div className="grid grid-cols-5 gap-8 w-full mt-12">
          {playlists ? (
            playlists.items.map(({ id, images, name, tracks }, i) => (
              <div className="flex flex-col text-center" key={i}>
                <Link to={id}>
                <div className="shadow-lg relative w-full mb-2 opacity-1 hover:opacity-50 duration-150">
                  {images.length ? (
                    <img className="object-cover w-50 h-50" src={images[0].url} alt="Album Art" />
                  ) : (
                    <PlaceholderArtwork>
                      <PlaceholderContent>
                        <IconMusic />
                      </PlaceholderContent>
                    </PlaceholderArtwork>
                  )}
                  <div className="flex justify-center text-center w-full h-full top-0 right-0 bottom-0 left-0 text-lg text-white opacity-0 hover:opacity-75">
                    <i className="fas fa-info-circle" />
                  </div>
                </div>
                </Link>
                <div>
                  <PlaylistName to={id}>{name}</PlaylistName>
                  <TotalTracks>{tracks.total} Tracks</TotalTracks>
                </div>
              </div>
            ))
          ) : (
            <Loader />
          )}
        </div>
      </div>
    </main>
  );
};

export default Playlists;
