import React from 'react';
import { Link } from '@reach/router';

import {
  IconSpotify,
  IconUser,
  IconTime,
  IconMicrophone,
  IconPlaylist,
  IconMusic,
  IconGithub,
} from './icons';

import styled from 'styled-components/macro';
import { theme, mixins, media } from '../styles';
import SpotifyNavIcon from "./Nav/SpotifyNavIcon";
import GitHubNavIcon from "./Nav/GitHubNavIcon";
import MenuItemContainer from "./Nav/MenuItemContainer";
const { colors } = theme;

// const Container = styled.nav`
