import React, { useState, useEffect } from 'react';
import { Link } from '@reach/router';
import { getTopArtistsShort, getTopArtistsMedium, getTopArtistsLong } from '../../spotify';
import { catchErrors } from '../../utils';

import { IconInfo } from '../icons';
import Loader from '../Loader';
import Button from "../Layout/Button";
import DateRangeButtons from "./DateRangeButtons";


const TopArtistsPage = () => {
  const [topArtists, setTopArtists] = useState(null);
  const [activeRange, setActiveRange] = useState('long');

  const apiCalls = {
    long: getTopArtistsLong(),
    medium: getTopArtistsMedium(),
    short: getTopArtistsShort(),
  };

  useEffect(() => {
    const fetchData = async () => {
      const { data } = await getTopArtistsLong();
      setTopArtists(data);
    };
    catchErrors(fetchData());
  }, []);

  const changeRange = async range => {
    const { data } = await apiCalls[range];
    setTopArtists(data);
    setActiveRange(range);
  };

  const setRangeData = range => catchErrors(changeRange(range));

  return (
    <main className="w-full mx-auto p-24">
      <header className="flex justify-between text-center">
        <h2 className="text-7xl">Top Artists</h2>
        <DateRangeButtons activeRange={activeRange} setRangeData={setRangeData} />
      </header>
      <div className="grid grid-cols-5 gap-6 mt-12">
        {topArtists ? (
          topArtists.items.map(({ id, external_urls, images, name }, i) => (
            <div className="flex flex-col text-center items-center" key={i}>
              <Link to={`/artist/${id}`}>
                <div className="inline-block relative w-48 h-48">
                  {images.length && <img className="rounded-full object-cover w-48 h-48" src={images[1].url} alt="Artist" />}
                  <div className="flex flex-col justify-center text-center absolute h-full w-full bg-lightGrey top-0 bottom-0 right-0 left-0 rounded-full opacity-0 hover:opacity-75">
                    <div className="flex justify-center">
                      <IconInfo />
                    </div>
                  </div>
                </div>
              </Link>
              <a className="my-3 border-b-2 border-transparent hover:border-b-2 hover:border-solid hover:border-white" href={external_urls.spotify} target="_blank" rel="noopener noreferrer">
                {name}
              </a>
            </div>
          ))
        ) : (
          <div className="col-span-4">
          <Loader />
          </div>
        )}
      </div>
    </main>
  );
};

export default TopArtistsPage;
