import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import { formatWithCommas, catchErrors } from '../utils';
import { getArtist } from '../spotify';

import Loader from './Loader';

import styled from 'styled-components/macro';
import { theme, mixins, media, Main } from '../styles';
const { colors, fontSizes, spacing } = theme;

const Stats = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  grid-gap: 10px;
  margin-top: ${spacing.md};
  text-align: center;
`;
const Stat = styled.div``;
const Number = styled.div`
  color: ${colors.blue};
  font-weight: 700;
  font-size: ${fontSizes.lg};
  text-transform: capitalize;
  ${media.tablet`
    font-size: ${fontSizes.md};
  `};
`;
const Genre = styled.div`
  font-size: ${fontSizes.md};
`;
const NumLabel = styled.p`
  color: ${colors.lightGrey};
  font-size: ${fontSizes.xs};
  text-transform: uppercase;
  letter-spacing: 1px;
  margin-top: ${spacing.xs};
`;

const Artist = props => {
  const { artistId } = props;
  const [artist, setArtist] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const { data } = await getArtist(artistId);
      setArtist(data);
    };
    catchErrors(fetchData());
  }, [artistId]);

  return (
    <>
      {artist ? (
        <main className="w-full mx-auto flex flex-col text-center h-full">
          <div className="w-full">
            <img src={artist.images[0].url} alt="Artist Artwork" className="w-72 rounded-full mx-auto mt-8" />
          </div>
          <div>
            <h1 className="text-7xl mt-8">{artist.name}</h1>
            <div className="grid grid-cols-3 gap-4 mt-3 text-center">
              <div>
                <Number>{formatWithCommas(artist.followers.total)}</Number>
                <NumLabel>Followers</NumLabel>
              </div>
              {artist.genres && (
                <Stat>
                  <Number>
                    {artist.genres.map(genre => (
                      <Genre key={genre}>{genre}</Genre>
                    ))}
                  </Number>
                  <NumLabel>Genres</NumLabel>
                </Stat>
              )}
              {artist.popularity && (
                <Stat>
                  <Number>{artist.popularity}%</Number>
                  <NumLabel>Popularity</NumLabel>
                </Stat>
              )}
            </div>
          </div>
        </main>
      ) : (
        <Loader />
      )}
    </>
  );
};

Artist.propTypes = {
  artistId: PropTypes.string,
};

export default Artist;
