import {IconInfo} from "../icons";

export default function TrackArtwork({ track, imageSize }) {
  let imgSize
  switch (imageSize) {
    case 'small':
      imgSize = 2
      break;
    case 'medium':
      imgSize = 1
      break;
    case 'large':
      imgSize = 0
      break;
    default:
      imgSize = 2
  }

  console.log(track)
  return (
    <div>
      <div className={ imgSize === 2 ? "inline-block relative w-20" : imgSize === 1 ? "inline-block relative w-36" : "inline-block relative w-48"}>
        {track.album.images.length && <img className="w-full" src={track.album.images[imgSize].url} alt="Album Artwork" />}
        <div className="flex justify-center text-center absolute w-full h-full top-0 bottom-0 left-0 right-0 text-white opacity-0">
          <IconInfo />
        </div>
      </div>
    </div>
  )
}
