import React, { useState, useEffect } from 'react';
import { Link } from '@reach/router';
import { getUserInfo, logout } from '../spotify';
import { catchErrors } from '../utils';

import Button from './Layout/Button'

import { IconUser } from './icons';
import Loader from './Loader';
import TrackItem from './TrackItem';

import styled from 'styled-components/macro';
import { theme, mixins, media, Main } from '../styles';
import TopArtists from "./Profile/TopArtists";
const { colors, fontSizes, spacing } = theme;


const Tracklist = styled.div`
  ${media.tablet`
    &:last-of-type {
      margin-top: 50px;
    }
  `};
`;
const TracklistHeading = styled.div`
  ${mixins.flexBetween};
  margin-bottom: 40px;
  h3 {
    display: inline-block;
    margin: 0;
  }
`;
const MoreButton = styled(Link)`
  ${mixins.button};
  text-align: center;
  white-space: nowrap;
  ${media.phablet`
    padding: 11px 20px;
    font-sizes: ${fontSizes.xs};
  `};
`;
const Mask = styled.div`
  ${mixins.flexCenter};
  position: absolute;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.5);
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  color: ${colors.white};
  opacity: 0;
  transition: ${theme.transition};
  svg {
    width: 25px;
  }
`;
const Artist = styled.li`
  display: flex;
  align-items: center;
  margin-bottom: ${spacing.md};
  ${media.tablet`
    margin-bottom: ${spacing.base};
  `};
  &:hover,
  &:focus {
    ${Mask} {
      opacity: 1;
    }
  }
`;
const ArtistArtwork = styled(Link)`
  display: inline-block;
  position: relative;
  width: 50px;
  min-width: 50px;
  margin-right: ${spacing.base};
  img {
    width: 50px;
    min-width: 50px;
    height: 50px;
    margin-right: ${spacing.base};
    border-radius: 100%;
  }
`;

const ArtistName = styled(Link)`
  flex-grow: 1;
  span {
    border-bottom: 1px solid transparent;
    &:hover,
    &:focus {
      border-bottom: 1px solid ${colors.white};
    }
  }
`;

const User = () => {
  const [user, setUser] = useState(null);
  const [followedArtists, setFollowedArtists] = useState(null);
  const [playlists, setPlaylists] = useState(null);
  const [topArtists, setTopArtists] = useState(null);
  const [topTracks, setTopTracks] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const { user, followedArtists, playlists, topArtists, topTracks } = await getUserInfo();
      setUser(user);
      setFollowedArtists(followedArtists);
      setPlaylists(playlists);
      setTopArtists(topArtists);
      setTopTracks(topTracks);
    };
    catchErrors(fetchData());
  }, []);

  const totalPlaylists = playlists ? playlists.total : 0;

  const profileStats = [
    {
      label: "Followers",
      number: user?.followers?.total,
      showIf: user
    },
    {
      label: "Following",
      number: followedArtists?.artists?.items.length,
      showIf: followedArtists
    },
    {
      label: "Playlist",
      number: totalPlaylists,
      showIf: totalPlaylists
    },
  ]

  return (
    <React.Fragment>
      {user ? (
        <Main>
          <div className="flex justify-between align-middle">
            <div className="w-36 h-36 rounded-full">
              {user.images.length > 0 ? (
                <img src={user.images[0].url} alt="avatar" />
              ) : (
                <div className="border-8 border-solid rounded-full w-full h-full">
                  <div className="w-28 h-28 mx-auto">
                  <IconUser />
                  </div>
                </div>
              )}
            </div>
            <a className="hover:text-offGreen" href={user.external_urls.spotify} target="_blank" rel="noopener noreferrer">
              <h1 className="text-7xl font-semibold mt-10">{user.display_name}</h1>
            </a>
            <div className="grid grid-cols-3 gap-12 mt-9">
              {profileStats.map((stat) => {
                if (stat.showIf) {
                  return (
                    <div key={stat.label} className="text-center">
                      <div className="text-green font-semibold text-lg">{stat.number}</div>
                      <div className="text-lightGrey text-sm uppercase mt-1">{stat.label}</div>
                    </div>
                  )
                } else {
                  return;
                }
              })}
            </div>
            <div>
              <Button onClickHandler={logout}>
                Logout
              </Button>
            </div>
          </div>

          <div className="grid grid-cols-2 gap-12 w-full mt-24">

            <TopArtists
              topArtists={topArtists}
              Artist={Artist}
              ArtistArtwork={ArtistArtwork}
              Mask={Mask}
              ArtistName={ArtistName}
            />

            <Tracklist>
              <TracklistHeading>
                <h3>Top Tracks of All Time</h3>
                <MoreButton to="/tracks">See More</MoreButton>
              </TracklistHeading>
              <ul>
                {topTracks ? (
                  topTracks.items
                    .slice(0, 10)
                    .map((track, i) => <TrackItem track={track} key={i} />)
                ) : (
                  <Loader />
                )}
              </ul>
            </Tracklist>
          </div>
        </Main>
      ) : (
        <Loader />
      )}
    </React.Fragment>
  );
};

export default User;
