export default function Button({children, onClickHandler, styleType="none", activeRange}) {

  const styles = {
    "none": "bg-transparent text-white border-2 solid border-white rounded-md mt-12 py-6 px-12 text-xs font-semibold uppercase align-middle",
    "pill": "bg-transparent border-2 border-2 border-white border-solid px-6 rounded-full uppercase text-sm text-white",
    "underline-when-active": activeRange ? "bg-transparent text-white border-b-2 border-white border-solid px-4 pb-0 rounded-none" : "bg-transparent text-lightGrey border-b-2 border-transparent border-solid p-4"
  }

  return (
    <>
      <button
        className={styles[styleType]}
        onClick={onClickHandler}
      >
        {children}
      </button>
    </>
  )
}
