import {IconInfo} from "../icons";
import Loader from "../Loader";
import React from "react";
import Button from "../Layout/Button";
import {Link} from "@reach/router";

export default function TopArtists({ topArtists, Artist, ArtistArtwork, Mask, ArtistName }){
  return (
    <div>
      <div className="flex justify-between text-center inline-block">
        <h3>Top Artists of All Time</h3>
        <Button styleType="pill">
          <Link to={"/artists"}>
            See More
          </Link>
        </Button>
      </div>
      <div>
        {topArtists ? (
          <ul className="mx-12">
            {topArtists.items.slice(0, 10).map((artist, i) => (
              <li key={i} className="flex text-center justify-around mx-auto mb-6">
                <ArtistArtwork to={`/artist/${artist.id}`}>
                  {artist.images.length && <img src={artist.images[2].url} alt="Artist" />}
                  <Mask>
                    <IconInfo />
                  </Mask>
                </ArtistArtwork>
                <ArtistName to={`/artist/${artist.id}`}>
                  <span>{artist.name}</span>
                </ArtistName>
              </li>
            ))}
          </ul>
        ) : (
          <Loader />
        )}
      </div>
    </div>
  )
}
