import React from 'react';
import { Link } from '@reach/router';

import {
  IconSpotify,
  IconUser,
  IconTime,
  IconMicrophone,
  IconPlaylist,
  IconMusic,
  IconGithub,
} from './icons';

import styled from 'styled-components/macro';
import { theme, mixins, media } from '../styles';
import SpotifyNavIcon from "./Nav/SpotifyNavIcon";
const { colors } = theme;

// const Container = styled.nav`
//   box-shadow: 0 0 10px rgba(0, 0, 0, 0.3);
//   display: flex;
//   justify-content: space-between;
//   align-items: center;
//   flex-direction: column;
//   min-height: 100vh;
//   position: fixed;
//   top: 0;
//   left: 0;
//   width: ${theme.navWidth};
//   background-color: ${colors.navBlack};
//   text-align: center;
//   z-index: 99;
//   ${media.tablet`
//     top: auto;
//     bottom: 0;
//     right: 0;
//     width: 100%;
//     min-height: ${theme.navHeight};
//     height: ${theme.navHeight};
//     flex-direction: row;
//   `};
//   & > * {
//     width: 100%;
//     ${media.tablet`
//       height: 100%;
//     `};
//   }
// `;
// const Logo = styled.div`
//   color: ${colors.green};
//   margin-top: 30px;
//   width: 70px;
//   height: 70px;
//   transition: ${theme.transition};
//   ${media.tablet`
//     display: none;
//   `};
//   &:hover,
//   &:focus {
//     color: ${colors.offGreen};
//   }
//   svg {
//     width: 50px;
//   }
// `;
const Github = styled.div`
  color: ${colors.lightGrey};
  width: 45px;
  height: 45px;
  margin-bottom: 30px;
  ${media.tablet`
    display: none;
  `};
  a {
    &:hover,
    &:focus,
    &.active {
      color: ${colors.blue};
    }
    svg {
      width: 30px;
    }
  }
`;
const Menu = styled.ul`
  display: flex;
  flex-direction: column;
  ${media.tablet`
    flex-direction: row;
    align-items: flex-end;
    justify-content: center;
  `};
`;
const MenuItem = styled.li`
  color: ${colors.lightGrey};
  font-size: 11px;
  ${media.tablet`
    flex-grow: 1;
    flex-basis: 100%;
    height: 100%;
  `};
  a {
    display: block;
    padding: 15px 0;
    border-left: 5px solid transparent;
    width: 100%;
    height: 100%;
    ${media.tablet`
      ${mixins.flexCenter};
      flex-direction: column;
      padding: 0;
      border-left: 0;
      border-top: 3px solid transparent;
    `};
    &:hover,
    &:focus,
    &.active {
      color: ${colors.white};
      background-color: ${colors.black};
      border-left: 5px solid ${colors.offGreen};
      ${media.tablet`
        border-left: 0;
        border-top: 3px solid ${colors.offGreen};
      `};
    }
  }
  svg {
    width: 20px;
    height: 20px;
    margin-bottom: 7px;
  }
`;

const isActive = ({ isCurrent }) => (isCurrent ? { className: 'active' } : null);

const NavLink = props => <Link getProps={isActive} {...props} />;

const menuData = [
  {
    name: "Profile",
    href: "/",
    icon: <IconUser />
  },
  {
    name: "Artists",
    href: "Artists",
    icon: <IconMicrophone />
  },
  {
    name: "Top Tracks",
    href: "tracks",
    icon: <IconMusic />
  },
  {
    name: "Recent",
    href: "recent",
    icon: <IconTime />
  },
  {
    name: "Playlists",
    href: "playlists",
    icon: <IconPlaylist />
  },
]

const Nav = () => (
  <div className="shadow-md shadow-black flex flex-col justify-between min-h-full fixed top-0 left-0 w-24">

    <div className="mx-auto">
      <SpotifyNavIcon />
    </div>
    <Menu>
      {menuData.map((item) => (
        <MenuItem>
          <NavLink to={item.href}>
            <div className="flex justify-center">
              {item.icon}
            </div>
            <div>{item.name}</div>
          </NavLink>
        </MenuItem>
      ))}
    </Menu>

    <Github>
      <a
        href="https://github.com/sandypockets"
        target="_blank"
        rel="noopener noreferrer">
        <IconGithub />
      </a>
    </Github>
  </div>
);

export default Nav;
