import { useState, useEffect } from 'react';
import { getUserInfo, logout } from '../spotify';
import { catchErrors } from '../utils';

import Button from './Layout/Button'

import Loader from './Loader';

import styled from 'styled-components/macro';
import { theme, mixins, Main } from '../styles';
import TopArtists from "./Profile/TopArtists";
import TopTracks from "./Profile/TopTracks";
import ProfileStats from "./Profile/ProfileStats";
import ProfileIcon from "./Profile/ProfileIcon";
import ProfileName from "./Profile/ProfileName";
const { colors } = theme;


const Mask = styled.div`
  ${mixins.flexCenter};
  position: absolute;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.5);
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  color: ${colors.white};
  opacity: 0;
  transition: ${theme.transition};
  svg {
    width: 25px;
  }
`;

const User = () => {
  const [user, setUser] = useState(null);
  const [followedArtists, setFollowedArtists] = useState(null);
  const [playlists, setPlaylists] = useState(null);
  const [topArtists, setTopArtists] = useState(null);
  const [topTracks, setTopTracks] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const { user, followedArtists, playlists, topArtists, topTracks } = await getUserInfo();
      setUser(user);
      setFollowedArtists(followedArtists);
      setPlaylists(playlists);
      setTopArtists(topArtists);
      setTopTracks(topTracks);
    };
    catchErrors(fetchData());
  }, []);

  const totalPlaylists = playlists ? playlists.total : 0;

  return (
    <>
      {user ? (
        <Main>
          <div className="flex justify-between align-middle">
            <ProfileIcon user={user} />
            <ProfileName user={user} />
            <ProfileStats user={user} followedArtists={followedArtists} totalPlaylists={totalPlaylists} />
            <div>
              <Button onClickHandler={logout}>
                Logout
              </Button>
            </div>
          </div>
          <div className="grid grid-cols-2 gap-12 w-full mt-24">
            <TopArtists topArtists={topArtists} Mask={Mask} />
            <TopTracks topTracks={topTracks} />
          </div>
        </Main>
      ) : (
        <Loader />
      )}
    </>
  );
};

export default User;
